mapper.update:
UPDATE bookings
SET user_id = #{userId},
    business_day_slot_id = #{businessDaySlotId},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT * FROM bookings WHERE business_day_slot_id IN
#{item}
ORDER BY business_day_slot_id

SELECT *
FROM bookings
WHERE user_id = #{userId}
  AND business_day_slot_id = #{businessDaySlotId}
  AND status = 'reserved'
LIMIT 1
