mapper.update:
UPDATE bookings
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT *
FROM bookings
WHERE business_date = #{date}
  AND time_slot_id IN

    #{item}

ORDER BY time_slot_id

SELECT *
FROM bookings
WHERE user_id = #{userId}
  AND business_date = #{businessDate}
  AND time_slot_id = #{timeSlotId}
  AND status = 'reserved'
LIMIT 1

SELECT *
FROM bookings
WHERE business_date = #{businessDate}
  AND time_slot_id = #{timeSlotId}
  AND status = 'reserved'
LIMIT 1

SELECT *
FROM bookings
WHERE user_id = #{userId}
  AND business_date = #{businessDate}
  AND status = 'reserved'
LIMIT 1
